/*!
 * Ajax Crud
 * =================================
 * author aslinye cuma beliau masih bootstrap 3
 * Use for johnitvn/yii2-ajaxcrud extension
 * @author John Martin john.itvn@gmail.com
 */ $(document).ready(function () {
  (modal = new ModalRemote(
    $("#ajaxCrubModal").length > 0 && 0 == $("#ajaxCrudModal").length
      ? "#ajaxCrubModal"
      : "#ajaxCrudModal"
  )),
    $(document).on("click", '[role="modal-remote"]', function (e) {
      e.preventDefault(), modal.open(this, null);
    }),
    $(document).on("click", '[role="modal-remote-bulk"]', function (e) {
      e.preventDefault();
      var t = [],
        o = "selection";
      null != $(this).data("selector") && (o = $(this).data("selector")),
        $('input:checkbox[name="' + o + '[]"]').each(function () {
          this.checked && t.push($(this).val());
        }),
        0 == t.length
          ? (modal.show(),
            modal.setTitle("No selection"),
            modal.setContent("You must select item(s) to use this action"),
            modal.addFooterButton("Close", "btn btn-default", function (e, t) {
              modal.hide();
            }))
          : modal.open(this, t);
    });
});
